import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import EditCard from '../EditCard/EditCard';
import React from 'react';

const Card = props => {
    const { title, children, text, isCollapsedCard, setIsCollapsedCard, ...configCardProps } = props;

    return (
        <>
            <div className='bg-white rounded-xl shadow p-5 w-full sm:w-80 md:w-86 h-36'>
                <div className='flex justify-between mb-2'>
                    <h1 className='text-lg font-medium'>{title}</h1>
                    <div className='flex'>
                        <div className='mr-5' onClick={() => setIsCollapsedCard(!isCollapsedCard)}>
                            <FontAwesomeIcon icon={'pencil-alt'} className='fa-xs text-gray-300 cursor-pointer' />
                        </div>
                        <div>
                            {/*<FontAwesomeIcon icon={'circle'} className='text-gray-500' />*/}
                            <FontAwesomeIcon icon={'dot-circle'} className='text-green-500' />
                            <FontAwesomeIcon icon={'dot-circle'} className='text-gray-300' />
                        </div>
                    </div>
                </div>
                <div className='text-sm text-gray-400 mb-3'>{children}</div>
                <span className='text-sm text-gray-400 font-medium'>{text}</span>
            </div>
        </>
    );
};

Card.propTypes = {
    title: PropTypes.string,
    text: PropTypes.string,
};

export default Card;
