import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { MESSAGE_TYPE } from '../../../config/constants';
import { useSelector } from 'react-redux';

const ICONS = {
    [MESSAGE_TYPE.success]: 'check-circle',
    [MESSAGE_TYPE.warning]: 'exclamation-circle',
    [MESSAGE_TYPE.error]: 'times',
};

const Messages = () => {
    const messages = useSelector(state => state.components.messages);

    return (
        <div className={'flex items-center flex-col absolute w-full pointer-events-none'}>
            {messages.map(message => {
                return (
                    <div className={`bg-white shadow-md rounded px-5 py-3 flex items-center justify-center my-2`}>
                        <FontAwesomeIcon icon={ICONS[message.type]} className='fa-lg text-green-500 mr-3' />
                        <span>{message.message}</span>
                    </div>
                );
            })}
        </div>
    );
};

Messages.propTypes = {};

export default Messages;
