import PropTypes from 'prop-types';
import Header from '../Header/Header';
import Sidebar from '../Sidebar/Sidebar';
import { useState } from 'react';
import useWindowSize from '../../hooks/useWindowSize';
import { BREAKPOINTS } from '../../config/constants';
import MobileMenu from '../MobileMenu/MobileMenu';
import useDrawer from '../../hooks/useDrawer';

const Layout = props => {
    const [isCollapsed, setIsCollapsed] = useState(false);

    const { children, ...layoutProps } = props;

    const { width: windowWidth } = useWindowSize();

    const [isVisibleMobileMenuDrawer, openMobileMenuDrawer, closeMobileMenuDrawer] = useDrawer();

    return (
        <div className={'min-h-screen flex'}>
            {windowWidth > BREAKPOINTS.lg ? (
                <Sidebar isCollapsed={isCollapsed} setIsCollapsed={setIsCollapsed} />
            ) : (
                <MobileMenu
                    isVisibleMobileMenuDrawer={isVisibleMobileMenuDrawer}
                    openMobileMenuDrawer={openMobileMenuDrawer}
                    closeMobileMenuDrawer={closeMobileMenuDrawer}
                />
            )}
            <div className={'flex flex-auto flex-col'}>
                <Header openMobileMenuDrawer={openMobileMenuDrawer} />
                <main className='flex-auto transition-all duration-300 bg-gray-100' {...layoutProps}>
                    {children}
                </main>
            </div>
        </div>
    );
};

Layout.propTypes = {};

export default Layout;
