import React, { useState } from 'react';
import PropTypes from 'prop-types';

const Tooltip = props => {
    const [showTooltip, setShowTooltip] = useState(false);

    const { text, children, className, ...tooltipProps } = props;

    return (
        <div {...tooltipProps}>
            <div className='relative' onClick={() => setShowTooltip(!showTooltip)}>
                <span
                    className={`bg-gray-600 opacity-60 text-white py-1 text-center w-36 absolute bottom-5 rounded ${className}`}
                >
                    {'text'}
                </span>
                {children}
            </div>
        </div>
    );
};

Tooltip.propTypes = {};

export default Tooltip;
