import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { ROUTES } from '../../config/constants';
import logo from '../../assets/images/logo.svg';
import Menu from '../shared/Menu/Menu';
import SidebarMenu from './SidebarMenu/SidebarMenu';

const Sidebar = props => {
    const { isCollapsed, setIsCollapsed } = props;

    return (
        <div
            className={`min-h-screen pt-8 shadow transition-all duration-300 ease-in-out ${
                isCollapsed ? 'w-24' : 'w-72'
            }`}
        >
            <FontAwesomeIcon
                icon={isCollapsed ? 'chevron-circle-right' : 'chevron-circle-left'}
                className={`fa-2x text-indigo-600 shadow-sm cursor-pointer mt-2 bg-white absolute  transition-all duration-300 ease-in-out ${
                    isCollapsed ? 'left-20' : 'left-64 ml-4'
                }`}
                onClick={() => setIsCollapsed(!isCollapsed)}
            />

            <div className='flex items-center justify-start px-4 ml-1 mb-10'>
                <img className='w-12 h-12' src={logo} alt='Logo' />
                <span
                    className={`text-gray-600 ml-14 text-2xl font-bold absolute transition-opacity delay-150 duration-300  ${
                        isCollapsed ? ' opacity-0 invisible' : ''
                    }`}
                >
                    Crypto Trader
                </span>
            </div>

            <SidebarMenu isCollapsed={isCollapsed} setIsCollapsed={setIsCollapsed} />
        </div>
    );
};

Sidebar.propTypes = {};

export default Sidebar;
