import PropTypes from 'prop-types';
import { useState } from 'react';
import Tabs from '../shared/Tabs/Tabs';
import TabPane from '../shared/TabPane/TabPane';

const Settings = props => {
    const [isEdited, setIsEdited] = useState(true);

    return (
        <div className='flex w-full h-full text-gray-100'>
            <div className='w-2/5 p-10 pr-0 bg-gray-800 border-r border-gray-600'>
                <h1 className='text-2xl font-medium pb-6 mb-10 border-b border-gray-600'>Settings</h1>
                <Tabs className={'text-gray-300'}>
                    <TabPane icon={'user-circle'} className={'hover:text-gray-100'}>
                        Account Settings
                    </TabPane>
                    <TabPane icon={'shield-alt'} className={'hover:text-gray-100'}>
                        Security
                    </TabPane>
                    <TabPane icon={['fab', 'telegram']}>Telegram</TabPane>
                </Tabs>
            </div>
            <div className='w-3/5 p-10 bg-gray-800 pl-8'>
                <h1 className='text-2xl font-medium'>{`Account Settings`}</h1>
            </div>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;

