import PropTypes from 'prop-types';
import { DRAWER_SIDE } from '../../../config/constants';

const Drawer = props => {
    const { isCollapsed, setIsCollapsed, children, className, ...drawerProps } = props;

    const DRAWER_STYLES = {
        [DRAWER_SIDE.left]: ` w-9/12 h-screen space-y-6 py-7 px-2 fixed inset-y-0 left-0 transform transition duration-300 ease-in-out z-50 ${
            isCollapsed ? '-translate-x-0' : '-translate-x-full'
        } ${className}`,

        [DRAWER_SIDE.right]: `w-9/12 h-screen space-y-6 py-7 px-2 fixed inset-y-0 right-0  transform transition duration-300 ease-in-out z-50 ${
            isCollapsed ? '-translate-x-0' : 'translate-x-full'
        } ${className}`,
    };

    return (
        <>
            <div
                className={`bg-black  h-full w-full fixed right-0 top-0 z-40 transition-all duration-300 ease-in-out ${
                    isCollapsed ? 'opacity-50' : 'opacity-0 invisible'
                } `}
                onClick={() => setIsCollapsed(!isCollapsed)}
            />
            <div className={`[DRAWER_STYLES] ${className}`}>{children}</div>
        </>
    );
};

Drawer.propTypes = {};

export default Drawer;
