import PropTypes from 'prop-types';
import Drawer from '../../shared/Drawer/Drawer';
import { DRAWER_SIDE } from '../../../config/constants';
import Tag from '../../shared/Tag/Tag';
import SwitchButton from './SwitchButton/SwitchButton';
import FormItem from '../../shared/Form/FormItem/FormItem';
import Input from '../../shared/Input/Input';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const EditConfiguration = props => {
    const { isEditDrawer, closeEditDrawer } = props;

    return (
        <Drawer side={DRAWER_SIDE.right} isVisible={isEditDrawer} closeDrawer={closeEditDrawer} header>
            <div>
                <div className='flex items-center justify-between mb-14'>
                    <div className='flex items-center'>
                        <h1 className='text-2xl font-medium mr-3'>{'Configuration Name'}</h1>
                        <FontAwesomeIcon icon='pencil-alt' className='text-gray-400 fa-sm' />
                    </div>
                    <Tag color={'green'}>active</Tag>
                </div>

                <div className='mb-14'>
                    <div className='flex items-center justify-between mb-6'>
                        <h1 className='text-xl mr-10'>Spot</h1>
                        <SwitchButton />
                    </div>
                    <div className='flex mb-5'>
                        <FormItem
                            label={'Trade Amount'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2 mr-8'}
                        >
                            <Input type={'number'} className={'h-8'} min={1} />
                        </FormItem>
                        <FormItem
                            label={'Take Profit Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2'}
                        >
                            <Input type={'number'} className={'h-8'} min={0.1} />
                        </FormItem>
                    </div>
                    <div className='flex'>
                        <FormItem
                            label={'Stop Loss Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2 mr-8'}
                        >
                            <Input type={'number'} className={'h-8'} min={0.1} />
                        </FormItem>
                        <FormItem
                            label={'Daily Trade Limit'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2'}
                        >
                            <Input type={'number'} className={'h-8'} min={1} step={1} />
                        </FormItem>
                    </div>
                </div>

                <div className='mb-14'>
                    <div className='flex items-center justify-between mb-6'>
                        <h1 className='text-xl mr-10'>Futures</h1>
                        <SwitchButton />
                    </div>
                    <div className='flex mb-5'>
                        <FormItem
                            label={'Trade Amount'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2 mr-8'}
                        >
                            <Input type={'number'} className={'h-8'} min={1} />
                        </FormItem>
                        <FormItem label={'Leverage'} helpMessage={'Please fill out this field'} className={'w-1/2'}>
                            <Input type={'number'} className={'h-8'} min={1} max={125} step={1} />
                        </FormItem>
                    </div>
                    <div className='flex mb-5'>
                        <FormItem
                            label={'Take Profit Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2 mr-8'}
                        >
                            <Input type={'number'} className={'h-8'} min={0.1} />
                        </FormItem>
                        <FormItem
                            label={'Stop Loss Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2'}
                        >
                            <Input type={'number'} className={'h-8'} min={0.1} />
                        </FormItem>
                    </div>
                    <div className='flex'>
                        <FormItem
                            label={'Daily Trade Limit'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2 mr-8'}
                        >
                            <Input type={'number'} className={'h-8'} min={1} step={1} />
                        </FormItem>
                        <FormItem
                            label={'Manage profit'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2'}
                        >
                            <select
                                className={
                                    'h-8 border border-gray-200 rounded focus:ring focus:ring-indigo-300 px-2 py-1 outline-none'
                                }
                            >
                                <option>Accumulate profit</option>
                                <option>Transfer to Spot wallet</option>
                            </select>
                        </FormItem>
                    </div>
                </div>

                <div className='mb-14'>
                    <div className='flex items-center justify-between mb-6'>
                        <h1 className='text-xl mr-10'>Global</h1>
                        <SwitchButton />
                    </div>
                    <div className='flex'>
                        <FormItem
                            label={'Concurrent Trades Limit'}
                            helpMessage={'Please fill out this field'}
                            className={'w-1/2 mr-8'}
                        >
                            <Input type={'number'} className={'h-8'} min={1} step={1} />
                        </FormItem>
                    </div>
                </div>
            </div>
        </Drawer>
    );
};

EditConfiguration.propTypes = {};

export default EditConfiguration;
