import PropTypes from 'prop-types';
import { useState } from 'react';

const useModal = props => {
    const [isVisible, setIsVisible] = useState(false);

    const showModal = () => {
        setIsVisible(true);
    };

    const hideModal = () => {
        setIsVisible(false);
    };

    const onOk = () => {
        setIsVisible(false);
    };

    const onCancel = () => {
        setIsVisible(false);
    };

    return [isVisible, showModal, hideModal, onOk, onCancel];
};

useModal.propTypes = {};

export default useModal;
