import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

const Item = props => {
    const { className, children, icon, ...itemProps } = props;
    return (
        <Link>
            <div
                className={`hover:text-gray-800 hover:bg-gray-100 cursor-pointer flex items-center p-2 my-6 transition-colors duration-200  text-gray-400 rounded-lg ${className}`}
                {...itemProps}
            >
                {icon ? <img src={icon} alt='icon' className='w-6' /> : null}
                <span className='mx-4 text-lg font-normal'>{children}</span>
            </div>
        </Link>
    );
};

Item.propTypes = {
    className: PropTypes.string,
};

export default Item;
