import PropTypes from 'prop-types';
import classNames from 'classnames';

const MenuItem = props => {
    const { children, className, icon, path = '#', isMenuCollapsed, select } = props;

    return (
        <div
            className={classNames(
                'flex items-center w-full p-2 hover:text-gray-800 hover:bg-gray-100 rounded-lg cursor-pointer transition-colors duration-200',
                className,
                {
                    'bg-indigo-600': select,
                    'bg-white': !select,
                }
            )}
        >
            {icon}
            <span
                className={classNames('mx-4 text-lg font-normal transition-opacity duration-300', {
                    'opacity-0 invisible': isMenuCollapsed,
                    'opacity-100 visible': !isMenuCollapsed,
                })}
            >
                {children}
            </span>
        </div>
    );
};

MenuItem.propTypes = {
    className: PropTypes.string,
};

export default MenuItem;
