import PropTypes from 'prop-types';
import { SIDE } from '../../../config/constants';

const Drawer = props => {
    const { isEditDrawerVisible, onEditDrawerClose, children, className, side, ...drawerProps } = props;

    const DRAWER_STYLES = {
        [SIDE.left]: ` w-9/12 h-screen space-y-6 py-7 px-2 fixed inset-y-0 left-0 transform transition duration-300 ease-in-out z-50 ${
            isEditDrawerVisible ? '-translate-x-0' : '-translate-x-full'
        } ${className}`,

        [SIDE.right]: `w-6/12 h-screen space-y-6 py-7 px-2 fixed inset-y-0 right-0  transform transition duration-300 ease-in-out z-50 ${
            isEditDrawerVisible ? '-translate-x-0' : 'translate-x-full'
        } ${className}`,
    };

    return (
        <>
            <div
                className={`bg-black  h-full w-full fixed right-0 top-0 z-40 transition-all duration-300 ease-in-out ${
                    isEditDrawerVisible ? 'opacity-50' : 'opacity-0 invisible'
                } `}
                onClick={onEditDrawerClose}
            />
            <div className={`${DRAWER_STYLES[side]} ${className}`}>{children}</div>
        </>
    );
};

Drawer.propTypes = {
    className: PropTypes.string,
    side: PropTypes.oneOf(Object.values(SIDE)),
};

export default Drawer;
