import { useState } from 'react';
import PropTypes from 'prop-types';
import logo from '../../assets/images/logo.svg';
import arrowLeft from '../../assets/icons/Arrows/arrow-left.svg';
import arrowRight from '../../assets/icons/Arrows/arrow-right.svg';
import Menu from '../shared/Menu/Menu';

const Sidebar = props => {
    const [isCollapsed, setIsCollapsed] = useState(false);

    return (
        <div
            className={`h-full fixed bg-white pt-8 shadow transition-all duration-300 ease-in-out ${
                isCollapsed ? 'w-24' : 'w-72'
            }`}
        >
            <img
                src={isCollapsed ? arrowRight : arrowLeft}
                className={`w-8 border border-transparent rounded-full shadow cursor-pointer p-1 mt-2 bg-white absolute  ml-4 transition-all duration-300 ease-in-out ${
                    isCollapsed ? 'left-12' : 'left-64'
                }`}
                alt='icon'
                onClick={() => setIsCollapsed(!isCollapsed)}
            />

            <div className='flex items-center justify-start px-4'>
                <img className='w-12 h-12' src={logo} alt='Logo' />
                <span
                    className={`text-gray-600 ml-14 text-2xl font-bold  transition-opacity delay-150 duration-300 fixed ${
                        isCollapsed ? ' opacity-0 invisible' : ''
                    }`}
                >
                    Crypto Trader
                </span>
            </div>

            <Menu className={'mt-10 px-6'}>
                <Menu.Item icon={'folder'} isCollapsed={isCollapsed}>
                    MenuItem1
                </Menu.Item>
                <Menu.SubMenu title={'SubMenu1'} icon={menuIcon} isCollapsed={isCollapsed}>
                    <Menu.Item isCollapsed={isCollapsed}>SubMenuItem1</Menu.Item>
                    <Menu.Item isCollapsed={isCollapsed}>SubMenuItem2</Menu.Item>
                </Menu.SubMenu>
                <Menu.Item icon={menuIcon} isCollapsed={isCollapsed}>
                    MenuItem2
                </Menu.Item>
                <Menu.SubMenu title={'SubMenu2'} icon={menuIcon} isCollapsed={isCollapsed}>
                    <Menu.Item isCollapsed={isCollapsed}>SubMenuItem3</Menu.Item>
                </Menu.SubMenu>
            </Menu>
        </div>
    );
};

Sidebar.propTypes = {};

export default Sidebar;
