import React, { useState } from 'react';
import PropTypes from 'prop-types';
import loginBackground from '../../assets/images/login-background.svg';
import logo from '../../assets/images/logo.svg';
import Input from '../shared/Input/Input';
import Button from '../shared/Button/Button';
import { BUTTON_TYPES, ROUTES } from '../../config/constants';
import { Link, useHistory } from 'react-router-dom';
import FormItem from '../shared/Form/FormItem/FormItem';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useDispatch } from 'react-redux';
import { useForm } from 'react-hook-form';
import { register } from '../../redux/actions/auth';

const Register = props => {
    const [isShowingPassword, setIsShowingPassword] = useState(false);

    const onShowPassword = () => {
        setIsShowingPassword(!isShowingPassword);
    };

    const dispatch = useDispatch();
    const history = useHistory();

    const {
        register: registerFormField,
        handleSubmit,
        formState: { errors },
    } = useForm();

    const onFormSubmit = async values => {
        const user = await dispatch(register(values));

        if (user) {
            history.push(ROUTES.dashboard.path);
        }
    };

    return (
        <div className='bg-gray-200 flex h-screen md:h-auto'>
            <div className='hidden md:block bg-indigo-500 w-full min-h-screen py-12 px-12'>
                <div className='flex items-center mb-16'>
                    <img src={logo} alt='Logo' className='w-12 mr-3' />
                    <h1 className='text-2xl font-bold text-white'>Crypto Trader</h1>
                </div>
                <div className='text-white font-semibold leading-24 mb-20 md:text-2xl xl:text-3xl'>
                    A few clicks away from creating your Crypto Trader
                </div>
                <img src={loginBackground} alt='Login Background Img' className='w-full xl:w-5/6 2xl:w-9/12 mx-auto' />
            </div>
            <div className='bg-white w-full md:max-w-sm lg:max-w-2xl 2xl:max-w-3xl pt-8'>
                <div className='max-w-sm w-10/12 sm:max-w-lg lg:3/5 h-full mx-auto flex flex-col justify-between pt-10'>
                    <div className='mx-auto sm:px-10 md:px-0 lg:px-6'>
                        <h2 className='text-3xl md:text-3xl text-gray-700 font-bold tracking-wide mb-20'>Register</h2>
                        <div className='mb-8'>
                            <h3 className='text-2xl text-gray-700 font-medium mb-3'>
                                Manage all your lottery efficiently
                            </h3>
                            <p className='text-gray-600 font-medium'>
                                Let's get you all set up so you can verify your personal account and begin setting up
                                your profile
                            </p>
                        </div>
                        <form onSubmit={handleSubmit(onFormSubmit)}>
                            <div className='mb-8'>
                                <div className='flex flex-col mb-4 w-full'>
                                    <FormItem
                                        label={'First Name'}
                                        hasValidationError={errors.first_name}
                                        helpMessage={'Please fill out this field.'}
                                    >
                                        <Input
                                            type={'text'}
                                            placeholder={'First Name'}
                                            {...registerFormField('first_name', { required: true })}
                                        />
                                    </FormItem>
                                </div>
                                <div className='flex flex-col mb-4 w-full'>
                                    <FormItem
                                        label={'Last Name'}
                                        hasValidationError={errors.last_name}
                                        helpMessage={'Please fill out this field.'}
                                    >
                                        <Input
                                            type={'text'}
                                            placeholder={'Last Name'}
                                            {...registerFormField('last_name', { required: true })}
                                        />
                                    </FormItem>
                                </div>
                                <div className='flex flex-col mb-4 w-full'>
                                    <FormItem
                                        label={'Email'}
                                        hasValidationError={errors.email}
                                        helpMessage={'Please fill out this field.'}
                                    >
                                        <Input
                                            type={'email'}
                                            placeholder={'Email'}
                                            {...registerFormField('email', { required: true })}
                                        />
                                    </FormItem>
                                </div>
                                <div className='flex flex-col mb-4 w-full relative'>
                                    <FormItem
                                        label={'Password'}
                                        hasValidationError={errors.password}
                                        helpMessage={'Please fill out this field.'}
                                    >
                                        <Input
                                            type={`${isShowingPassword ? 'text' : 'password'}`}
                                            placeholder={'Password'}
                                            {...registerFormField('password', { required: true })}
                                        />
                                        <FontAwesomeIcon
                                            icon={`${isShowingPassword ? 'eye' : 'eye-slash'}`}
                                            className='absolute right-3 top-9'
                                            onClick={onShowPassword}
                                        />
                                    </FormItem>
                                </div>
                            </div>
                            <Button
                                type={BUTTON_TYPES.primary}
                                className={'w-full mb-6 bg-indigo-600 hover:bg-indigo-700'}
                            >
                                {'Create Account'}
                            </Button>
                        </form>
                    </div>
                    <div className='text-center text-gray-700 text-lg font-medium pb-6'>
                        <span className='mr-1'>Already have an account yet?</span>
                        <Link to={ROUTES.login.path}>
                            <Button type={BUTTON_TYPES.link}>{'Log in'}</Button>
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

Register.propTypes = {};

export default Register;
