import PropTypes from 'prop-types';
import { useState } from 'react';
import Tabs from '../shared/Tabs/Tabs';
import TabPane from '../shared/TabPane/TabPane';
import Section from './Section/Section';
import Account from './Section/Account/Account';
import Tooltip from '../shared/Tooltip/Tooltip';
import Button from '../shared/Button/Button';

const Settings = props => {
    const [isEdited, setIsEdited] = useState(true);

    return (
        <div className='flex w-full h-full text-gray-100'>
            <div className='w-2/5 2xl:w-4/12 p-10 xl:pl-16 pr-0 border-r border-gray-300'>
                <Section title={'Settings'}>
                    <Tabs className={'text-gray-600 2xl:pr-4'}>
                        <TabPane icon={'user-circle'} className={'hover:text-gray-900'}>
                            Account Settings
                        </TabPane>
                        <TabPane icon={'shield-alt'} className={'hover:text-gray-900'}>
                            Security
                        </TabPane>
                        <TabPane icon={['fab', 'telegram']} className={'hover:text-gray-900'}>
                            Telegram
                        </TabPane>
                    </Tabs>
                </Section>
            </div>
            <div className='w-3/5 2xl:flex-grow p-10 pb-3 pl-10 pr-0 flex flex-col justify-between'>
                <Section title={'Account Settings'} className={'border-b border-gray-300 pb-12'}>
                    <Account />
                </Section>

                <div className='flex justify-end pr-16'>
                    {isEdited ? (
                        <Button
                            type={'primary'}
                            className={'w-32 rounded bg-indigo-700'}
                            onClick={() => setIsEdited(!isEdited)}
                        >
                            Edit
                        </Button>
                    ) : (
                        <div>
                            <Button type={'link'} className={'mr-8'} onClick={() => setIsEdited(!isEdited)}>
                                Cancel
                            </Button>
                            <Button
                                type={'primary'}
                                className={'w-24 rounded-full bg-indigo-600'}
                                onClick={() => setIsEdited(!isEdited)}
                            >
                                Save
                            </Button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;

