import React from 'react';
import PropTypes from 'prop-types';
import Input from '../Input/Input';

const Modal = props => {
    const { isShowed, setIsShowed, children, className, ...modalProps } = props;

    return (
        <div
            className={`w-full h-full fixed bg-black opacity-30 flex justify-center items-center transition-all duration-300 ease-in-out ${
                isShowed ? 'opacity-50' : 'opacity-0 invisible'
            } ${className}`}
            onClick={() => setIsShowed(!isShowed)}
        >
            <div className='w-2/5 bg-white rounded shadow p-8'>{children}</div>
        </div>
    );
};

Modal.propTypes = {};

export default Modal;
