import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Configuration = props => {
    const { name, description, text, openEditDrawer } = props;

    return (
        <>
            <div className='bg-white flex flex-col rounded-xl shadow p-5 w-full sm:w-5/12 lg:w-3/12 h-36'>
                <div className='flex justify-between mb-2'>
                    <h1 className='text-lg font-medium'>{name}</h1>
                    <div className='flex'>
                        <div className='mr-5' onClick={openEditDrawer}>
                            <FontAwesomeIcon icon={'pencil-alt'} className='fa-xs text-gray-300 cursor-pointer' />
                        </div>
                        <div>
                            <FontAwesomeIcon icon={'dot-circle'} className='text-green-500' />
                            <FontAwesomeIcon icon={'dot-circle'} className='text-gray-300' />
                        </div>
                    </div>
                </div>
                <span className='text-sm text-gray-400 mb-3'>{description}</span>
                <span className='text-sm text-gray-400 font-medium'>{text}</span>
            </div>
        </>
    );
};

Configuration.propTypes = {
    title: PropTypes.string,
    text: PropTypes.string,
};

export default Configuration;
