import PropTypes from 'prop-types';
import Menu from '../Menu/Menu';

const Drawer = props => {
    const { isCollapsed, setIsCollapsed, ...drawerProps } = props;

    return (
        <>
            <div
                className={`bg-black opacity-60 h-full absolute right-0 top-0 z-40 transition transition-all duration-500 ${
                    isCollapsed ? 'w-full' : 'hidden'
                } `}
            />
            <div
                className={`absolute top-0 h-full w-0 bg-white z-50  shadow transition transition-all duration-500  ${
                    isCollapsed ? 'w-9/12 bg-red-300' : 'w-0 -right-70 '
                } `}
            >
                <Menu>
                    <Menu.Item>item1</Menu.Item>
                </Menu>
            </div>
        </>
    );
};

Drawer.propTypes = {};

export default Drawer;
