import React, { useState } from 'react';
import PropTypes from 'prop-types';
import logo from '../../assets/images/logo.svg';
import logOut from '../../assets/icons/MenuItemIcons/log-out.svg';
import config from '../../assets/icons/MenuItemIcons/config.svg';
import Menu from '../shared/Menu/Menu';
import Popover from '../shared/Popover/Popover';

const ListItems = [
    {
        title: 'Settings',
        icon: config,
    },
    {
        title: 'Log out',
        icon: logOut,
    },
];

const Header = props => {
    const [show, setShow] = useState(true);
    return (
        <div className='bg-white w-full fixed flex flex-row-reverse pl-72 pr-10 py-8'>
            <div className='flex flex-col items-end'>
                <div className='block relative cursor-pointer' onClick={() => setShow(!show)}>
                    <img src={logo} className='mx-auto object-cover rounded-full w-12' alt='profile' />
                </div>
                <Popover
                    className={`transition-opacity duration-500 ease-in-out ${show ? 'opacity-0 invisible' : ' '}`}
                >
                    {ListItems.map((listItem, index) => {
                        return (
                            <Menu.Item className={'mt-0 mb-0 pl-0'} icon={listItem.icon} key={index}>
                                {listItem.title}
                            </Menu.Item>
                        );
                    })}
                </Popover>
            </div>
        </div>
    );
};

Header.propTypes = {};

export default Header;
