export const BUTTON_TYPES = {
    primary: 'primary',
    link: 'link',
};

export const DRAWER_SIDE = {
    left: 'left',
    right: 'right',
};

export const TOOLTIP_SIDE = {
    top: 'top',
    right: 'right',
    bottom: 'bottom',
    left: 'left',
};

export const BREAKPOINTS = {
    sm: 640,
    md: 768,
    lg: 1024,
    xl: 1280,
    '2xl': 1536,
};

export const MESSAGE_TYPE = {
    success: 'success',
    warning: 'warning',
    error: 'error',
};

export const SPINNER_SIZES = {
    small: 'small',
    medium: 'medium',
    large: 'large',
};

export const ROUTES = {
    login: {
        path: '/login',
    },
    register: {
        path: '/register',
    },
    resetPassword: {
        path: '/reset-password',
    },
    settings: {
        path: '/app/settings',
    },
    settingsAccount: {
        path: '/app/settings/account',
    },
    settingsSecurity: {
        path: '/app/settings/security',
    },
    settingsTelegram: {
        path: '/app/settings/telegram',
    },
    configurations: {
        path: '/app/configurations',
    },
};
