import PropTypes from 'prop-types';
import { useState } from 'react';
import Input from '../../../shared/Input/Input';
import Button from '../../../shared/Button/Button';
import { BUTTON_TYPES } from '../../../../config/constants';
import FormItem from '../../../shared/Form/FormItem/FormItem';

const Account = props => {
    const [isEditing, setIsEditing] = useState(true);

    const editAccount = () => {
        setIsEditing(!isEditing);
    };

    return (
        <div className='h-full flex flex-col'>
            <h1 className='text-2xl text-gray-800 font-medium pb-6 mb-10 border-b border-gray-300'>Account Settings</h1>
            <div className='max-w-3xl pr-10 xl:pr-16'>
                <div className='border-b border-gray-300 pb-12 mb-12'>
                    <h2 className='mb-7 text-xl text-gray-800'>Personal Information</h2>
                    <div className='flex-col'>
                        <div className='lg:flex mb-6'>
                            <div className='flex flex-col lg:w-1/2 lg:mr-6 mb-6 lg:mb-0'>
                                <FormItem label={'First Name'}>
                                    <Input
                                        type={'text'}
                                        placeholder={'First Name'}
                                        id={'firstName'}
                                        className={'border-gray-200 text-gray-700 w-full'}
                                    />
                                </FormItem>
                            </div>
                            <div className='flex flex-col lg:w-1/2'>
                                <FormItem label={'Last Name'}>
                                    <Input
                                        type={'text'}
                                        placeholder={'Last Name'}
                                        id={'lastName'}
                                        className={'border-gray-200 text-gray-700 w-full'}
                                    />
                                </FormItem>
                            </div>
                        </div>
                        <div className='flex flex-col'>
                            <FormItem label={'Email'}>
                                <Input
                                    type={'email'}
                                    placeholder={'Email'}
                                    id={'email'}
                                    className={'border-gray-200 text-gray-700 w-full'}
                                />
                            </FormItem>
                        </div>
                    </div>
                </div>

                <div>
                    <h2 className='mb-7 text-xl text-black'>Binance API</h2>
                    <div className='lg:flex mb-7'>
                        <div className='flex flex-col lg:w-1/2 lg:mr-6 mb-6'>
                            <FormItem label={'API key'}>
                                <Input
                                    type={'text'}
                                    placeholder={'API key'}
                                    id={'apiKey'}
                                    className={'border-gray-200 text-gray-700 w-full'}
                                />
                            </FormItem>
                        </div>
                        <div className='flex flex-col lg:w-1/2'>
                            <FormItem label={'Secret Key'}>
                                <Input
                                    type={'text'}
                                    placeholder={'Secret Key'}
                                    id={'secretKey'}
                                    className={'border-gray-200 text-gray-700 w-full'}
                                />
                            </FormItem>
                        </div>
                    </div>
                </div>

                <div className='flex justify-end'>
                    <Button
                        type={BUTTON_TYPES.primary}
                        className={'bg-red-600 hover:bg-red-700 focus:bg-red-700 w-40 justify-self-end'}
                    >
                        Delete Account
                    </Button>
                </div>
            </div>

            <div className='border-t border-gray-300 pt-8 mt-9'>
                <div className='max-w-3xl pr-10 xl:pr-16 flex justify-end'>
                    {isEditing ? (
                        <Button
                            type={'primary'}
                            className={'w-56 rounded bg-indigo-700'}
                            onClick={() => setIsEditing(!isEditing)}
                        >
                            Edit
                        </Button>
                    ) : (
                        <div className='flex justify-between w-full xl:w-10/12'>
                            <Button type={'link'} className={''} onClick={() => setIsEditing(!isEditing)}>
                                Discard Changes
                            </Button>
                            <Button
                                type={'primary'}
                                className={'w-32 lg:w-56 rounded bg-indigo-700'}
                                onClick={() => setIsEditing(!isEditing)}
                            >
                                Save Changes
                            </Button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

Account.propTypes = {};

export default Account;
