import PropTypes from 'prop-types';
import { ROUTES } from '../../config/constants';
import Layout from '../Layout/Layout';
import PrivateRoute from '../shared/PrivateRoute/PrivateRoute';
import Dashboard from '../Dashboard/Dashboard';
import Settings from '../Settings/Settings';
import Configurations from '../Configurations/Configurations';
import { useDispatch } from 'react-redux';
import { fetchCurrentUser } from '../../redux/actions/auth';
import { useEffect } from 'react';

const PrivateRoutes = props => {
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(fetchCurrentUser());
    }, []);

    return (
        <Layout>
            <PrivateRoute exact path={ROUTES.dashboard.path} component={Dashboard} isAuthenticated={true} />
            <PrivateRoute path={ROUTES.settings.path} component={Settings} isAuthenticated={true} />
            <PrivateRoute exact path={ROUTES.configurations.path} component={Configurations} isAuthenticated={true} />
        </Layout>
    );
};

PrivateRoutes.propTypes = {};

export default PrivateRoutes;
