import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

const MenuItem = props => {
    const { className, children, icon, toggle, ...itemProps } = props;
    return (
        <Link to={'#'}>
            <div
                className={`hover:text-gray-800 hover:bg-gray-100 cursor-pointer flex items-center p-2 my-6 transition-colors duration-200  text-gray-400 rounded-lg ${className}`}
                {...itemProps}
            >
                {icon ? <img src={icon} alt='icon' className='w-6' /> : null}
                <span
                    className={`mx-4 text-lg font-normal ${
                        toggle ? ' opacity-0 invisible' : 'transition-opacity  duration-300'
                    }`}
                >
                    {children}
                </span>
            </div>
        </Link>
    );
};

MenuItem.propTypes = {
    className: PropTypes.string,
};

export default MenuItem;
