import React from 'react';
import PropTypes from 'prop-types';
import { STATUS } from '../../../config/constants';

const TAG_STYLES = {
    [STATUS.active]: `border-${color}-500 bg-${color}-100`,
};

const Tag = props => {
    const { color, children, ...tagProps } = props;

    return (
        <span className={`border  py-1 px-2 text-sm`} {...tagProps}>
            {children}
        </span>
    );
};

Tag.propTypes = {};

export default Tag;
