import { showMessageComponent } from './components';
import { MESSAGE_TYPE } from '../../config/constants';
import UserService from '../../services/UserService';

export const updateUser = userData => async dispatch => {
    try {
        const response = await UserService.updateUser(userData);

        const updatedUser = response.data;

        return updatedUser;
    } catch (error) {
        const errorResponseMessage = error.response?.data.error.message;

        console.error(errorResponseMessage || error);

        dispatch(showMessageComponent(MESSAGE_TYPE.error, errorResponseMessage));
    }
};

export const deleteUser = userId => async dispatch => {
    try {
        const response = await UserService.deleteUser(userId);

        const deletedUser = response.data;

        return deletedUser;
    } catch (error) {
        const errorResponseMessage = error.response?.data.error.message;

        console.error(errorResponseMessage || error);

        dispatch(showMessageComponent(MESSAGE_TYPE.error, errorResponseMessage));
    }
};
